sql.where:
T.IS_VALID = '1'

	 AND T.HELP_PROJECT_ID = #{helpProjectId,jdbcType=DECIMAL} 


	 AND T.UU_ID = #{uuId,jdbcType=VARCHAR} 


	 AND T.HELP_PROJECT_NAME LIKE '%'||#{helpProjectName,jdbcType=VARCHAR}||'%' 


	 AND T.PROJECT_TYPE = #{projectType,jdbcType=VARCHAR} 


	 AND T.PRIJECT_STATUS = #{prijectStatus,jdbcType=VARCHAR} 


	 AND T.ORG_CODE LIKE #{orgCode,jdbcType=VARCHAR}||'%'

mapper.delete:
UPDATE
	T_HELP_PROJECT
SET IS_VALID = '0', UPDATE_TIME = SYSDATE, UPDATOR = #{updator,jdbcType=DECIMAL}
WHERE
	UU_ID= #{uuId,jdbcType=DECIMAL}
