sql.where:
			T.IS_VALID = '1'

				AND UUID=#{uuid,jdbcType=VARCHAR}


				AND CODE=#{code,jdbcType=VARCHAR}


                AND REGION_CODE=#{regionCode,jdbcType=VARCHAR}


				AND TYPE=#{type,jdbcType=VARCHAR}


				AND URL=#{url,jdbcType=VARCHAR}


				AND IS_ENABLE=#{isEnable,jdbcType=VARCHAR}


				AND REMARK=#{remark,jdbcType=VARCHAR}


				AND IS_VALID=#{isValid,jdbcType=VARCHAR}


				AND UPDATOR=#{updator,jdbcType=DECIMAL}

mapper.delete:
UPDATE
	T_INDEX_CONTENT
SET IS_VALID = '0', UPDATE_TIME = SYSDATE, UPDATOR = #{updator,jdbcType=DECIMAL}
WHERE
	UUID= #{uuid,jdbcType=DECIMAL}
