sql.where:
T.IS_VALID = '1'


	 AND T.ATTRACTIONS_DESC  LIKE '%'|| #{attractionsDesc,jdbcType=VARCHAR} ||'%'  



	 AND T.ORG_CODE LIKE #{orgCode,jdbcType=VARCHAR}||'%' 



	 AND T.ISSU_STATUS = #{issuStatus} 



	 AND T.ATTRACTIONS_TYPE = #{attractionsType} 



	 AND T.CHARGE_TYPE = #{chargeType} 



	 AND  T.ATTRACTIONS_NAME LIKE '%'|| #{attractionsName} ||'%'

mapper.delete:
UPDATE
	T_DV_ATTRACTIONS_MANAGEMENT
SET IS_VALID = '0', UPDATE_TIME = SYSDATE, UPDATOR = #{updator,jdbcType=DECIMAL}
WHERE
	UUID= #{uuid,jdbcType=VARCHAR}
