mapper.select:
SELECT T.ORDER_ID, T.OUTER_CODE, T.PAY_STATUS, T.PAY_AMOUNT, T.PAY_TIME, T.UK_PRODUCT_ID, T.UK_PRODUCT_NAME, T.PRODUCT_GOODS, T.IS_VALID, T.CREATOR, T.CREATE_TIME, T.UPDATOR, T.UPDATE_TIME FROM T_ORDER_INFO T WHERE
ORDER_ID= #{orderId,jdbcType=DECIMAL}
OUTER_CODE = #{outerCode, jdbcType=VARCHAR}
AND T.IS_VALID = '1'

mapper.delete:
		UPDATE
			T_ORDER_INFO
		SET IS_VALID = '0', UPDATE_TIME = SYSDATE, UPDATOR = #{updator,jdbcType=DECIMAL}
		WHERE

				ORDER_ID= #{orderId,jdbcType=DECIMAL} 


                OUTER_CODE = #{outerCode, jdbcType=VARCHAR}
