mapper.select:
SELECT T.STORE_ID, T.BUSINESS_ID, T.BUSINESS_NAME, T.CODE, T.STATUS FROM T_STORE_INFO T WHERE
STORE_ID = #{storeId, jdbcType=DECIMAL}
BUSINESS_ID = #{businessID, jdbcType=VARCHAR}
AND T.IS_VALID = '1'

mapper.delete:
		UPDATE
			T_STORE_INFO
		SET IS_VALID = '0', UPDATE_TIME = SYSDATE, UPDATOR = #{updator,jdbcType=DECIMAL}
		WHERE

                STORE_ID = #{storeId, jdbcType=DECIMAL}


                BUSINESS_ID = #{businessID, jdbcType=VARCHAR}
